import React, { useState, useRef, useEffect } from 'react';
import ReactMarkdown from 'react-markdown';
import remarkGfm from 'remark-gfm';
import rehypeHighlight from 'rehype-highlight';
import './ChatInterface.css';

interface Message {
  id: string;
  content: string;
  isUser: boolean;
  timestamp: Date;
}

interface ChatInterfaceProps {
  apiKey: string;
  appId: string;
}

const ChatInterface: React.FC<ChatInterfaceProps> = ({ apiKey, appId }) => {
  const [messages, setMessages] = useState<Message[]>([]);
  const [inputValue, setInputValue] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const messagesEndRef = useRef<HTMLDivElement>(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!inputValue.trim() || isLoading) return;

    const userMessage: Message = {
      id: Date.now().toString(),
      content: inputValue,
      isUser: true,
      timestamp: new Date(),
    };

    setMessages(prev => [...prev, userMessage]);
    setInputValue('');
    setIsLoading(true);

    try {
      const response = await fetch('http://localhost:8000/chat', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          message: inputValue,
          api_key: apiKey,
          app_id: appId,
        }),
      });

      const data = await response.json();

      const aiMessage: Message = {
        id: (Date.now() + 1).toString(),
        content: data.success ? data.response : `ÈîôËØØ: ${data.message}`,
        isUser: false,
        timestamp: new Date(),
      };

      setMessages(prev => [...prev, aiMessage]);
    } catch (error) {
      const errorMessage: Message = {
        id: (Date.now() + 1).toString(),
        content: 'ÁΩëÁªúËøûÊé•Â§±Ë¥•ÔºåËØ∑Ê£ÄÊü•ÂêéÁ´ØÊúçÂä°ÊòØÂê¶Ê≠£Â∏∏ËøêË°å',
        isUser: false,
        timestamp: new Date(),
      };
      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsLoading(false);
    }
  };

  const handleReset = () => {
    setMessages([]);
  };

  const presetQuestions = [
    "‰ªÄ‰πàÊòØÊ≠£ÂΩìÈò≤Âç´Ôºü",
    "ÂêàÂêåËøùÁ∫¶ÁöÑÊ≥ïÂæãÂêéÊûúÊòØ‰ªÄ‰πàÔºü",
    "Â¶Ç‰ΩïÁî≥ËØ∑Âä≥Âä®‰ª≤Ë£ÅÔºü",
    "Á¶ªÂ©öË¥¢‰∫ßÂ¶Ç‰ΩïÂàÜÂâ≤Ôºü",
    "‰∫§ÈÄö‰∫ãÊïÖË¥£‰ªªÂ¶Ç‰ΩïËÆ§ÂÆöÔºü"
  ];

  const handlePresetQuestion = (question: string) => {
    setInputValue(question);
  };

  return (
    <div className="chat-interface">
      <div className="chat-header">
        <h1>ü§ñ ÁΩóÁøîÊ≥ïÂæãÁü•ËØÜÈ°æÈóÆ</h1>
        <p>‰∏ì‰∏öÁöÑÊ≥ïÂæãÈóÆÈ¢òËß£Á≠îÂä©ÊâãÔºå‰∏∫ÊÇ®Êèê‰æõÂáÜÁ°ÆÁöÑÊ≥ïÂæãÂª∫ËÆÆ</p>
        <button onClick={handleReset} className="reset-button">
          üîÑ ÈáçÊñ∞ÂºÄÂßã
        </button>
      </div>

      <div className="chat-messages">
        {messages.length === 0 ? (
          <div className="welcome-message">
            <h2>Ê¨¢Ëøé‰ΩøÁî®Ê≥ïÂæãÁü•ËØÜÂä©ÊâãÔºÅ</h2>
            <p>ÊàëÊòØÊÇ®ÁöÑ‰∏ì‰∏öÊ≥ïÂæãÈ°æÈóÆÔºåÂèØ‰ª•‰∏∫ÊÇ®Ëß£Á≠îÂêÑÁßçÊ≥ïÂæãÈóÆÈ¢ò„ÄÇ</p>
            <div className="preset-questions">
              <h3>Â∏∏ËßÅÈóÆÈ¢òÔºö</h3>
              <div className="question-grid">
                {presetQuestions.map((question, index) => (
                  <button
                    key={index}
                    className="preset-question"
                    onClick={() => handlePresetQuestion(question)}
                  >
                    {question}
                  </button>
                ))}
              </div>
            </div>
          </div>
        ) : (
          messages.map((message) => (
            <div
              key={message.id}
              className={`message ${message.isUser ? 'user-message' : 'ai-message'}`}
            >
              <div className="message-content">
                {message.isUser ? (
                  <div className="user-text">{message.content}</div>
                ) : (
                  <ReactMarkdown
                    remarkPlugins={[remarkGfm]}
                    rehypePlugins={[rehypeHighlight]}
                    className="markdown-content"
                  >
                    {message.content}
                  </ReactMarkdown>
                )}
              </div>
              <div className="message-time">
                {message.timestamp.toLocaleTimeString()}
              </div>
            </div>
          ))
        )}
        {isLoading && (
          <div className="message ai-message">
            <div className="message-content">
              <div className="typing-indicator">
                <span></span>
                <span></span>
                <span></span>
              </div>
            </div>
          </div>
        )}
        <div ref={messagesEndRef} />
      </div>

      <form onSubmit={handleSubmit} className="chat-input-form">
        <div className="input-container">
          <input
            type="text"
            value={inputValue}
            onChange={(e) => setInputValue(e.target.value)}
            placeholder="ËØ∑ËæìÂÖ•ÊÇ®ÁöÑÊ≥ïÂæãÈóÆÈ¢ò..."
            disabled={isLoading}
            className="chat-input"
          />
          <button
            type="submit"
            disabled={!inputValue.trim() || isLoading}
            className="send-button"
          >
            {isLoading ? 'ÂèëÈÄÅ‰∏≠...' : 'ÂèëÈÄÅ'}
          </button>
        </div>
      </form>
    </div>
  );
};

export default ChatInterface;
