import React, { useState, useEffect } from 'react';
import ApiKeyConfig from './components/ApiKeyConfig';
import ChatInterface from './components/ChatInterface';
import './App.css';

interface ApiCredentials {
  apiKey: string;
  appId: string;
}

const App: React.FC = () => {
  const [credentials, setCredentials] = useState<ApiCredentials | null>(null);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    // Ê£ÄÊü•Êú¨Âú∞Â≠òÂÇ®‰∏≠ÊòØÂê¶Êúâ‰øùÂ≠òÁöÑAPIÂØÜÈí•
    const savedApiKey = localStorage.getItem('apiKey');
    const savedAppId = localStorage.getItem('appId');
    
    if (savedApiKey && savedAppId) {
      // È™åËØÅ‰øùÂ≠òÁöÑÂØÜÈí•ÊòØÂê¶‰ªçÁÑ∂ÊúâÊïà
      validateCredentials(savedApiKey, savedAppId);
    } else {
      setIsLoading(false);
    }
  }, []);

  const validateCredentials = async (apiKey: string, appId: string) => {
    try {
      const response = await fetch('http://localhost:8000/validate-api-key', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          api_key: apiKey,
          app_id: appId,
        }),
      });

      const data = await response.json();

      if (data.success) {
        setCredentials({ apiKey, appId });
        localStorage.setItem('apiKey', apiKey);
        localStorage.setItem('appId', appId);
      } else {
        // Ê∏ÖÈô§Êó†ÊïàÁöÑÂØÜÈí•
        localStorage.removeItem('apiKey');
        localStorage.removeItem('appId');
      }
    } catch (error) {
      console.error('È™åËØÅAPIÂØÜÈí•Â§±Ë¥•:', error);
      // Ê∏ÖÈô§Êó†ÊïàÁöÑÂØÜÈí•
      localStorage.removeItem('apiKey');
      localStorage.removeItem('appId');
    } finally {
      setIsLoading(false);
    }
  };

  const handleConfigSuccess = (apiKey: string, appId: string) => {
    setCredentials({ apiKey, appId });
    localStorage.setItem('apiKey', apiKey);
    localStorage.setItem('appId', appId);
  };

  const handleLogout = () => {
    setCredentials(null);
    localStorage.removeItem('apiKey');
    localStorage.removeItem('appId');
  };

  if (isLoading) {
    return (
      <div className="app-loading">
        <div className="loading-container">
          <div className="loading-spinner"></div>
          <p>Ê≠£Âú®È™åËØÅAPIÂØÜÈí•...</p>
        </div>
      </div>
    );
  }

  if (!credentials) {
    return <ApiKeyConfig onConfigSuccess={handleConfigSuccess} />;
  }

  return (
    <div className="app">
      <div className="app-header">
        <h1>ü§ñ Ê≥ïÂæãÁü•ËØÜÂä©Êâã</h1>
        <button onClick={handleLogout} className="logout-button">
          üîÑ ÈáçÊñ∞ÈÖçÁΩÆ
        </button>
      </div>
      <ChatInterface apiKey={credentials.apiKey} appId={credentials.appId} />
    </div>
  );
};

export default App;
